using System;\nusing System.Data;\n\nclass Program\n{\n    static void Main()\n    {\n        // Sample DataTable creation for demonstration purposes\n        DataTable dtDataTable = new DataTable();\n        dtDataTable.Columns.Add("ID", typeof(int));\n        dtDataTable.Columns.Add("Note", typeof(string));\n        dtDataTable.Columns.Add("Detail", typeof(string));\n\n        // Adding some rows to the DataTable\n        dtDataTable.Rows.Add(1, "Note1", "Detail1");\n        dtDataTable.Rows.Add(2, "Note2", "Detail2");\n        dtDataTable.Rows.Add(3, "Note3", "Detail3");\n\n        // Looping through the rows of the DataTable\n        foreach (DataRow row in dtDataTable.Rows)\n        {\n            string strDetail = row["Detail"].ToString();\n            Console.WriteLine("Detail: " + strDetail);\n        }\n    }\n}