using System.IO;\nusing iTextSharp.text.pdf;\n\n// Method to remove hyperlinks from PDF\npublic byte[] RemoveHyperlinks(byte[] pdfBytes)\n{\n    using (MemoryStream outputStream = new MemoryStream())\n    {\n        PdfReader reader = new PdfReader(pdfBytes);\n        PdfStamper stamper = new PdfStamper(reader, outputStream);\n\n        int numberOfPages = reader.NumberOfPages;\n        for (int i = 1; i <= numberOfPages; i++)\n        {\n            PdfDictionary pageDict = reader.GetPageN(i);\n            PdfArray annotsArray = pageDict.GetAsArray(PdfName.ANNOTS);\n\n            if (annotsArray != null)\n            {\n                for (int j = 0; j < annotsArray.Size; j++)\n                {\n                    PdfDictionary annot = annotsArray.GetAsDict(j);\n                    if (annot.Get(PdfName.SUBTYPE).Equals(PdfName.LINK))\n                    {\n                        annotsArray.Remove(j);\n                        j--;  // Adjust index after removal\n                    }\n                }\n                if (annotsArray.Size == 0)\n                {\n                    pageDict.Remove(PdfName.ANNOTS);\n                }\n            }\n        }\n\n        stamper.Close();\n        reader.Close();\n        return outputStream.ToArray();\n    }\n}\n\n// Usage example\nbyte[] originalPdf = File.ReadAllBytes("input.pdf");\nbyte[] pdfWithoutLinks = RemoveHyperlinks(originalPdf);\nFile.WriteAllBytes("output.pdf", pdfWithoutLinks);