[TestClass]\n    public class IntegrationTests\n    {\n        [TestMethod]\n        public void Test_CreateItem()\n        {\n            using (var context = new MyContext())\n            {\n                var repository = new Repository(context);\n                var item = new Item { Name = "New Item" };\n                repository.AddItem(item);\n\n                var savedItem = context.Items.SingleOrDefault(i => i.Name == "New Item");\n                Assert.IsNotNull(savedItem);\n                Assert.AreEqual("New Item", savedItem.Name);\n            }\n        }\n    }