protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        List<UserDC> list = new List<UserDC>();\n        // Populate your list with data\n        GridView1.DataSource = list;\n        GridView1.DataBind();\n    }\n}\n\nprotected void GridView1_RowDataBound(object sender, GridViewRowEventArgs e)\n{\n    if (e.Row.RowType == DataControlRowType.DataRow)\n    {\n        // Hide the columns by setting their visibility to false\n        e.Row.Cells[0].Visible = false; // Hide the first column\n        e.Row.Cells[1].Visible = false; // Hide the second column\n        e.Row.Cells[2].Visible = false; // Hide the third column\n    }\n}