using System;\nusing System.Windows.Forms;\n\npublic class MainForm : Form\n{\n    public MainForm()\n    {\n        this.KeyDown += new KeyEventHandler(OnKeyDown);\n    }\n\n    private void OnKeyDown(object sender, KeyEventArgs e)\n    {\n        if (e.KeyCode == Keys.E && e.Modifiers == Keys.LWin)\n        {\n            // Windows Key + E pressed\n            MessageBox.Show("Windows Key + E pressed");\n        }\n        else if (e.KeyCode == Keys.R && e.Modifiers == Keys.LWin)\n        {\n            // Windows Key + R pressed\n            MessageBox.Show("Windows Key + R pressed");\n        }\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new MainForm());\n    }\n}