using System;\n\npublic class RecurrenceChecker\n{\n    public static bool IsDateInRecurrence(DateTime startDate, DateTime endDate, DateTime targetDate, TimeSpan recurrenceSpan)\n    {\n        if (targetDate < startDate)\n        {\n            return false;\n        }\n\n        TimeSpan timeSinceStart = targetDate - startDate;\n        long recurrenceTicks = recurrenceSpan.Ticks;\n        long elapsedRecurrences = timeSinceStart.Ticks / recurrenceTicks;\n        DateTime adjustedStartDate = startDate.AddTicks(elapsedRecurrences * recurrenceTicks);\n        DateTime adjustedEndDate = endDate.AddTicks(elapsedRecurrences * recurrenceTicks);\n\n        return targetDate >= adjustedStartDate && targetDate <= adjustedEndDate;\n    }\n\n    public static void Main()\n    {\n        DateTime startDate = new DateTime(2009, 1, 1, 1, 0, 0);\n        DateTime endDate = new DateTime(2009, 1, 6, 17, 0, 0);\n        DateTime targetDate = DateTime.Now; // Example target date\n        TimeSpan recurrenceSpan = TimeSpan.FromDays(365 * 2); // every 2 years\n\n        bool isInRecurrence = IsDateInRecurrence(startDate, endDate, targetDate, recurrenceSpan);\n        Console.WriteLine($"Is the target date within the recurrence: {isInRecurrence}");\n    }\n}