using System;\nusing System.Diagnostics;\nusing System.Threading.Tasks;\n\nTask.Run(() =>\n{\n    ProcessStartInfo startInfo = new ProcessStartInfo();\n    startInfo.FileName = "updater.exe";\n    startInfo.UseShellExecute = true; // This makes the process independent of the parent.\n    startInfo.CreateNoWindow = true;  // Optional: If you don't want to show the window.\n    startInfo.WindowStyle = ProcessWindowStyle.Hidden; // Optional: If you want to hide the window.\n\n    Process updaterProcess = Process.Start(startInfo);\n});\n\n// Exit the parent process if necessary\nEnvironment.Exit(0);