using System;\nusing System.Net;\n\nclass Program\n{\n    static void Main()\n    {\n        // Add SSL2 to the SecurityProtocol\n        ServicePointManager.SecurityProtocol = SecurityProtocolType.Ssl2;\n\n        // Create the HttpWebRequest\n        HttpWebRequest request = (HttpWebRequest)WebRequest.Create("https://192.168.x.x");\n        \n        // Ignore SSL certificate validation errors (for self-signed certificates)\n        ServicePointManager.ServerCertificateValidationCallback = (sender, certificate, chain, sslPolicyErrors) => true;\n\n        try\n        {\n            using (HttpWebResponse response = (HttpWebResponse)request.GetResponse())\n            {\n                // Process the response\n                Console.WriteLine(response.StatusCode);\n            }\n        }\n        catch (WebException ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n    }\n}