using Oracle.DataAccess.Types;\n   using Oracle.DataAccess.Client;\n\n   [OracleCustomTypeMapping("MYSCHEMA.MYUDT")]\n   public class MyUDT : IOracleCustomType, INullable\n   {\n       [OracleObjectMapping("ATTRIBUTE1")]\n       public string Attribute1 { get; set; }\n\n       [OracleObjectMapping("ATTRIBUTE2")]\n       public decimal Attribute2 { get; set; }\n\n       public bool IsNull { get; private set; }\n\n       public static MyUDT Null\n       {\n           get\n           {\n               MyUDT obj = new MyUDT();\n               obj.IsNull = true;\n               return obj;\n           }\n       }\n\n       public void FromCustomObject(OracleConnection con, IntPtr pUdt)\n       {\n           if (!IsNull)\n           {\n               OracleUdt.SetValue(con, pUdt, "ATTRIBUTE1", Attribute1);\n               OracleUdt.SetValue(con, pUdt, "ATTRIBUTE2", Attribute2);\n           }\n       }\n\n       public void ToCustomObject(OracleConnection con, IntPtr pUdt)\n       {\n           Attribute1 = (string)OracleUdt.GetValue(con, pUdt, "ATTRIBUTE1");\n           Attribute2 = (decimal)OracleUdt.GetValue(con, pUdt, "ATTRIBUTE2");\n       }\n   }