using System;\nusing System.Data;\n\npublic class Program\n{\n    public static void Main()\n    {\n        DataSet ds = new DataSet();\n        ds.Tables.Add(new DataTable());\n        ds.Tables[0].Columns.Add("column_1", typeof(string));\n        ds.Tables[0].Columns.Add("column_2", typeof(int));\n        ds.Tables[0].Columns.Add("column_4", typeof(string));\n\n        // Add the new column\n        DataColumn newColumn = ds.Tables[0].Columns.Add("column_3", typeof(string));\n\n        // Set the new column's ordinal to the desired position\n        newColumn.SetOrdinal(2); // This moves the column to the third position (0-based index)\n        \n        // Display the columns to verify the order\n        foreach (DataColumn column in ds.Tables[0].Columns)\n        {\n            Console.WriteLine(column.ColumnName);\n        }\n    }\n}