using System;\n\npublic class CirclePoint\n{\n    public static (double x, double y) CalculatePoint(double radius, double angle, (double x, double y)? origin = null)\n    {\n        var originPoint = origin ?? (0, 0);\n        double x = originPoint.x + radius * Math.Cos(angle);\n        double y = originPoint.y + radius * Math.Sin(angle);\n        return (x, y);\n    }\n\n    public static void Main()\n    {\n        var point = CalculatePoint(5, Math.PI / 4);\n        Console.WriteLine($"x: {point.x}, y: {point.y}");\n    }\n}