using System;\n\npublic class Program\n{\n    public static void Main()\n    {\n        Guid guid = Guid.NewGuid();\n        string base64Guid = Convert.ToBase64String(guid.ToByteArray());\n        \n        // Make the Base64 string URL-friendly\n        base64Guid = base64Guid.Replace("/", "_").Replace("+", "-").TrimEnd('=');\n        \n        Console.WriteLine("Original GUID: " + guid);\n        Console.WriteLine("Base64 Encoded GUID: " + base64Guid);\n    }\n}