using System;\nusing System.Collections.Generic;\nusing System.Web.UI;\nusing System.Web.UI.WebControls;\n\npublic partial class GridViewSum : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            // Bind GridView with sample data\n            GridView1.DataSource = GetSampleData();\n            GridView1.DataBind();\n        }\n    }\n\n    private List<Item> GetSampleData()\n    {\n        return new List<Item>\n        {\n            new Item { Price = 10.00M },\n            new Item { Price = 20.00M },\n            new Item { Price = 30.00M }\n        };\n    }\n\n    protected void GridView1_RowDataBound(object sender, GridViewRowEventArgs e)\n    {\n        if (e.Row.RowType == DataControlRowType.DataRow)\n        {\n            // Accumulate the total price\n            decimal total = (decimal)(ViewState["Total"] ?? 0M);\n            total += Convert.ToDecimal(DataBinder.Eval(e.Row.DataItem, "Price"));\n            ViewState["Total"] = total;\n        }\n        else if (e.Row.RowType == DataControlRowType.Footer)\n        {\n            // Update the TextBox with the total price\n            decimal total = (decimal)(ViewState["Total"] ?? 0M);\n            ScriptManager.RegisterStartupScript(this, GetType(), "updateTotal", $"updateTotal({total});", true);\n        }\n    }\n}\n\npublic class Item\n{\n    public decimal Price { get; set; }\n}