using System;\nusing System.Data;\nusing System.Data.SqlServerCe;\nusing System.IO;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string sourceDbPath = @"C:\path\to\your\copied\database.sdf";\n        string csvFilePath = @"C:\path\to\exported\data.csv";\n\n        string connectionString = $"Data Source={sourceDbPath};";\n\n        using (SqlCeConnection connection = new SqlCeConnection(connectionString))\n        {\n            connection.Open();\n\n            using (SqlCeCommand command = new SqlCeCommand("SELECT * FROM YourTableName", connection))\n            {\n                using (SqlCeDataReader reader = command.ExecuteReader())\n                {\n                    using (StreamWriter writer = new StreamWriter(csvFilePath))\n                    {\n                        // Write CSV header\n                        for (int i = 0; i < reader.FieldCount; i++)\n                        {\n                            writer.Write(reader.GetName(i));\n                            if (i < reader.FieldCount - 1)\n                                writer.Write(",");\n                        }\n                        writer.WriteLine();\n\n                        // Write CSV rows\n                        while (reader.Read())\n                        {\n                            for (int i = 0; i < reader.FieldCount; i++)\n                            {\n                                writer.Write(reader.GetValue(i).ToString());\n                                if (i < reader.FieldCount - 1)\n                                    writer.Write(",");\n                            }\n                            writer.WriteLine();\n                        }\n                    }\n                }\n            }\n        }\n\n        Console.WriteLine("Data exported to CSV successfully.");\n    }\n}