using Microsoft.Win32;\nusing System;\n\nclass Program\n{\n    static void Main()\n    {\n        string fileExtension = ".smc";\n        string progID = "SMCFile";\n        string applicationPath = @"C:\Path\To\YourApp.exe";\n\n        // Create the file extension key\n        RegistryKey key = Registry.ClassesRoot.CreateSubKey(fileExtension);\n        if (key != null)\n        {\n            key.SetValue("", progID);\n            key.Close();\n        }\n\n        // Create the ProgID key\n        key = Registry.ClassesRoot.CreateSubKey(progID);\n        if (key != null)\n        {\n            key.SetValue("", "SMC File");\n            key.Close();\n        }\n\n        // Create the shell\open\command key within the ProgID key\n        key = Registry.ClassesRoot.CreateSubKey(progID + @"\shell\open\command");\n        if (key != null)\n        {\n            key.SetValue("", "\"" + applicationPath + "\" \"%1\"");\n            key.Close();\n        }\n\n        Console.WriteLine("File association created successfully!");\n    }\n}