using System;\nusing System.Text;\n\npublic class EndianConversion\n{\n    public static byte[] GetLittleEndianBytes(int value)\n    {\n        byte[] bytes = BitConverter.GetBytes(value);\n        // Ensure the bytes are in little-endian order\n        if (BitConverter.IsLittleEndian)\n        {\n            return bytes;\n        }\n        else\n        {\n            Array.Reverse(bytes);\n            return bytes;\n        }\n    }\n\n    public static byte[] GetLittleEndianBytes(string value)\n    {\n        // Ensure the string is exactly 4 characters\n        if (value.Length != 4)\n        {\n            throw new ArgumentException("The string must be exactly 4 characters long.");\n        }\n        byte[] bytes = Encoding.ASCII.GetBytes(value);\n        return bytes;\n    }\n\n    public static void Main()\n    {\n        int intValue = 12345678; // Example integer\n        string stringValue = "ABCD"; // Example 4-character string\n\n        byte[] intBytes = GetLittleEndianBytes(intValue);\n        byte[] stringBytes = GetLittleEndianBytes(stringValue);\n\n        // You can now send `intBytes` and `stringBytes` over UDP/IP\n    }\n}