using System;\nusing System.Windows.Forms;\n\npublic class MainForm : Form\n{\n    private OcxLoader _ocxLoader = new OcxLoader();\n\n    public MainForm()\n    {\n        InitializeComponent();\n        LoadOcxAndInitialize();\n    }\n\n    private void LoadOcxAndInitialize()\n    {\n        string ocxPath = "path_to_your_ocx.ocx";\n        if (_ocxLoader.LoadOcx(ocxPath))\n        {\n            // Here you would get the necessary function pointers and interact with the OCX\n            IntPtr initProc = _ocxLoader.GetOcxProcAddress("InitFunctionName");\n            if (initProc != IntPtr.Zero)\n            {\n                // Call the init function or create the control dynamically\n                // Example: Call the Init function if it exists\n                // InitFunction initFunc = (InitFunction)Marshal.GetDelegateForFunctionPointer(initProc, typeof(InitFunction));\n                // initFunc();\n            }\n        }\n        else\n        {\n            MessageBox.Show("Failed to load OCX.");\n        }\n    }\n\n    protected override void OnFormClosed(FormClosedEventArgs e)\n    {\n        _ocxLoader.UnloadOcx();\n        base.OnFormClosed(e);\n    }\n}