using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class UrlTreeBuilder\n{\n    public TreeNode BuildTree(List<string> urls)\n    {\n        var root = new TreeNode("Root");\n\n        foreach (var url in urls)\n        {\n            var segments = url.Trim('/').Split('/');\n            AddToTree(root, segments, 0);\n        }\n\n        return root;\n    }\n\n    private void AddToTree(TreeNode node, string[] segments, int index)\n    {\n        if (index >= segments.Length)\n        {\n            return;\n        }\n\n        var segment = segments[index];\n        var childNode = node.Children.FirstOrDefault(n => n.Name == segment);\n\n        if (childNode == null)\n        {\n            childNode = new TreeNode(segment);\n            node.AddChild(childNode);\n        }\n\n        AddToTree(childNode, segments, index + 1);\n    }\n}