using System;\nusing System.Web.Services;\n\nnamespace YourClientNamespace\n{\n    [WebServiceBinding(Name = "YourServiceContract", Namespace = "http://yournamespace")]\n    public class YourServiceClient : SoapHttpClientProtocol\n    {\n        public YourServiceClient()\n        {\n            this.Url = "http://yourserviceurl/YourService.svc";\n        }\n\n        [SoapDocumentMethod("http://yournamespace/YourMethod", RequestNamespace = "http://yournamespace", ResponseNamespace = "http://yournamespace", Use = SoapBindingUse.Literal, ParameterStyle = SoapParameterStyle.Wrapped)]\n        public string YourMethod(string param)\n        {\n            object[] results = this.Invoke("YourMethod", new object[] { param });\n            return (string)results[0];\n        }\n    }\n}