using System.Net.Mail;\nusing Microsoft.Practices.EnterpriseLibrary.Logging;\nusing Microsoft.Practices.EnterpriseLibrary.Logging.Configuration;\n\npublic void SendHtmlEmail(string to, string subject, string htmlBody)\n{\n    // Create the mail message\n    MailMessage mailMessage = new MailMessage();\n    mailMessage.To.Add(to);\n    mailMessage.Subject = subject;\n    mailMessage.Body = htmlBody;\n    mailMessage.IsBodyHtml = true;\n\n    // Configure the email sender (use your SMTP settings)\n    SmtpClient smtpClient = new SmtpClient("yourSmtpServer");\n    smtpClient.Credentials = new System.Net.NetworkCredential("yourUsername", "yourPassword");\n\n    // Send the email\n    smtpClient.Send(mailMessage);\n}\n\n// Example usage\nSendHtmlEmail("recipient@example.com", "Test Email", "<h1>Hello World</h1><p>This is an HTML email.</p>");