var contacts = new List<Contact>\n{\n    new Contact { Id = 1, Name = "John Doe" },\n    new Contact { Id = 2, Name = "Jane Smith" }\n    // Other contacts\n};\n\nvar contactAddresses = new List<ContactAddress>\n{\n    new ContactAddress { Id = 1, ContactId = 1, Address = "123 Main St" },\n    new ContactAddress { Id = 2, ContactId = 2, Address = "456 Elm St" }\n    // Other addresses\n};\n\nvar query = from contact in contacts\n            join address in contactAddresses\n            on contact.Id equals address.ContactId\n            select new\n            {\n                Contact = contact,\n                Address = address.Address\n            };\n\nforeach (var item in query)\n{\n    Console.WriteLine($"Contact: {item.Contact.Name}, Address: {item.Address}");\n}