using System;\nusing System.Text;\n\npublic static class HtmlEntityEncoder\n{\n    public static string Encode(string text)\n    {\n        if (string.IsNullOrEmpty(text))\n            return text;\n\n        StringBuilder encoded = new StringBuilder();\n\n        foreach (char c in text)\n        {\n            if ((int)c > 127) // Characters above ASCII 127 need to be encoded\n            {\n                encoded.AppendFormat("&#{0};", (int)c);\n            }\n            else\n            {\n                encoded.Append(c); // Append normal ASCII characters as-is\n            }\n        }\n\n        return encoded.ToString();\n    }\n}\n\n// Example usage\nclass Program\n{\n    static void Main()\n    {\n        string text = "Ω π ℵ ∞ ♣ ♥ ♈ ♉ ♊ ♋ ♌ ♍ ♎ ♏ ♐ ♑ ♒ ♓";\n        string encodedText = HtmlEntityEncoder.Encode(text);\n        Console.WriteLine(encodedText);\n    }\n}