public interface ISessionService\n   {\n       string GetPartnerId();\n       string GetCurrentLoggedInUserEmail();\n   }\n\n   public class SessionService : ISessionService\n   {\n       private readonly HttpSessionStateBase _session;\n\n       public SessionService(HttpSessionStateBase session)\n       {\n           _session = session;\n       }\n\n       public string GetPartnerId()\n       {\n           return _session["partnerId"] as string;\n       }\n\n       public string GetCurrentLoggedInUserEmail()\n       {\n           return _session["currentLoggedInUserEmail"] as string;\n       }\n   }