public class BaseController : Controller\n   {\n       protected ISessionService SessionService { get; private set; }\n\n       public BaseController(ISessionService sessionService)\n       {\n           SessionService = sessionService;\n       }\n\n       protected override ViewResult View(string viewName, string masterName, object model)\n       {\n           if (model is ModelBase modelBase)\n           {\n               modelBase.PartnerId = SessionService.GetPartnerId();\n               modelBase.CurrentLoggedInUserEmail = SessionService.GetCurrentLoggedInUserEmail();\n           }\n           return base.View(viewName, masterName, model);\n       }\n\n       protected override ViewResult View(string viewName, object model)\n       {\n           return View(viewName, null, model);\n       }\n\n       protected override ViewResult View(object model)\n       {\n           return View(null, model);\n       }\n   }