using System;\nusing System.Windows.Forms;\nusing System.Xml;\n\npublic class ScriptTreeView\n{\n    public static void LoadScriptsIntoTreeView(TreeView treeView, string xmlFilePath)\n    {\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.Load(xmlFilePath);\n\n        XmlNode rootNode = xmlDoc.SelectSingleNode("/scripts");\n        if (rootNode != null)\n        {\n            foreach (XmlNode scriptNode in rootNode.ChildNodes)\n            {\n                TreeNode scriptTreeNode = new TreeNode(scriptNode.Attributes["name"].Value);\n                treeView.Nodes.Add(scriptTreeNode);\n                LoadSubscriptsAndRecords(scriptTreeNode, scriptNode);\n            }\n        }\n    }\n\n    private static void LoadSubscriptsAndRecords(TreeNode treeNode, XmlNode xmlNode)\n    {\n        foreach (XmlNode childNode in xmlNode.ChildNodes)\n        {\n            if (childNode.Name == "subscript")\n            {\n                TreeNode subscriptTreeNode = new TreeNode(childNode.Attributes["name"].Value);\n                treeNode.Nodes.Add(subscriptTreeNode);\n                LoadSubscriptsAndRecords(subscriptTreeNode, childNode);\n            }\n            else if (childNode.Name == "record")\n            {\n                TreeNode recordTreeNode = new TreeNode("Record " + childNode.Attributes["id"].Value);\n                treeNode.Nodes.Add(recordTreeNode);\n            }\n        }\n    }\n}\n\n// Usage example:\n// TreeView treeView = new TreeView();\n// ScriptTreeView.LoadScriptsIntoTreeView(treeView, "path_to_your_scripts.xml");