// Create the common event handlers\nprivate void OpenMenuItem_Click(object sender, EventArgs e)\n{\n    // Implement the open action\n}\n\nprivate void AddMenuItem_Click(object sender, EventArgs e)\n{\n    // Implement the add action\n}\n\nprivate void EditMenuItem_Click(object sender, EventArgs e)\n{\n    // Implement the edit action\n}\n\n// Initialize the menu items\nToolStripMenuItem mnuLinksOpen = new ToolStripMenuItem("Open", null, OpenMenuItem_Click);\nToolStripMenuItem mnuLinksAdd = new ToolStripMenuItem("Add", null, AddMenuItem_Click);\nToolStripMenuItem mnuLinksEdit = new ToolStripMenuItem("Edit", null, EditMenuItem_Click);\n\n// Add them to the toolbar menu\nToolStripMenuItem toolbarMenu = new ToolStripMenuItem("Links");\ntoolbarMenu.DropDownItems.Add(mnuLinksOpen);\ntoolbarMenu.DropDownItems.Add(new ToolStripSeparator());\ntoolbarMenu.DropDownItems.Add(mnuLinksAdd);\ntoolbarMenu.DropDownItems.Add(new ToolStripSeparator());\ntoolbarMenu.DropDownItems.Add(mnuLinksEdit);\n\n// Add them to the context menu\nContextMenuStrip ctxMnuLinks = new ContextMenuStrip();\nctxMnuLinks.Items.Add(mnuLinksOpen.Clone());\nctxMnuLinks.Items.Add(new ToolStripSeparator());\nctxMnuLinks.Items.Add(mnuLinksAdd.Clone());\nctxMnuLinks.Items.Add(new ToolStripSeparator());\nctxMnuLinks.Items.Add(mnuLinksEdit.Clone());\n\n// Attach the context menu to the panel\npanel.ContextMenuStrip = ctxMnuLinks;