using System;\nusing System.Collections.Generic;\nusing System.Data;\nusing System.Reflection;\n\npublic class Record\n{\n    public int ID { get; set; }\n    public bool Status { get; set; }\n    public string Message { get; set; }\n}\n\npublic DataSet ConvertToDataSet<T>(List<T> list)\n{\n    DataSet ds = new DataSet();\n    DataTable dt = new DataTable(typeof(T).Name);\n    ds.Tables.Add(dt);\n\n    // Get the properties of T\n    PropertyInfo[] properties = typeof(T).GetProperties();\n\n    // Add columns to the DataTable\n    foreach (PropertyInfo property in properties)\n    {\n        dt.Columns.Add(property.Name, property.PropertyType);\n    }\n\n    // Add rows to the DataTable\n    foreach (T item in list)\n    {\n        DataRow row = dt.NewRow();\n        foreach (PropertyInfo property in properties)\n        {\n            row[property.Name] = property.GetValue(item) ?? DBNull.Value;\n        }\n        dt.Rows.Add(row);\n    }\n\n    return ds;\n}\n\npublic static void Main()\n{\n    List<Record> records = new List<Record>\n    {\n        new Record { ID = 1, Status = true, Message = "message1" },\n        new Record { ID = 2, Status = false, Message = "message2" },\n        new Record { ID = 3, Status = true, Message = "message3" },\n        // Add more records as necessary\n    };\n\n    DataSet ds = new ConvertToDataSet<Record>(records);\n\n    // Now you can pass the DataSet to Aspose.Cells for further processing\n}