using System;\nusing System.Data.SqlClient;\nusing System.Security.Cryptography.X509Certificates;\n\npublic class CertificateStorage\n{\n    private string connectionString = "your_connection_string_here";\n\n    public void StoreCertificate(X509Certificate2 certificate)\n    {\n        byte[] certData = certificate.Export(X509ContentType.Pfx, "your_password_here");\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n            using (SqlCommand command = new SqlCommand("INSERT INTO Certificates (CertData) VALUES (@CertData)", connection))\n            {\n                command.Parameters.AddWithValue("@CertData", certData);\n                command.ExecuteNonQuery();\n            }\n        }\n    }\n\n    public X509Certificate2 RetrieveCertificate(int certId)\n    {\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n            using (SqlCommand command = new SqlCommand("SELECT CertData FROM Certificates WHERE CertId = @CertId", connection))\n            {\n                command.Parameters.AddWithValue("@CertId", certId);\n                byte[] certData = (byte[])command.ExecuteScalar();\n                return new X509Certificate2(certData, "your_password_here");\n            }\n        }\n    }\n}