using System;\nusing NAudio.CoreAudioApi;\n\nclass Program\n{\n    static void Main()\n    {\n        MMDeviceEnumerator deviceEnumerator = new MMDeviceEnumerator();\n        MMDevice defaultDevice = deviceEnumerator.GetDefaultAudioEndpoint(DataFlow.Render, Role.Console);\n\n        defaultDevice.AudioEndpointVolume.OnVolumeNotification += OnVolumeNotification;\n\n        // Keep the application running to listen for events\n        Console.WriteLine("Listening for headphone connection events. Press Enter to exit.");\n        Console.ReadLine();\n    }\n\n    private static void OnVolumeNotification(AudioVolumeNotificationData data)\n    {\n        Console.WriteLine("Volume changed: " + data.MasterVolume);\n    }\n}