using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\nstring connectionString = "your_connection_string";\nusing (SqlConnection connection = new SqlConnection(connectionString))\n{\n    SqlCommand command = new SqlCommand("YourStoredProcedure", connection);\n    command.CommandType = CommandType.StoredProcedure;\n\n    connection.Open();\n    \n    using (SqlDataReader reader = command.ExecuteReader())\n    {\n        // Read the first result set\n        DataTable customerTable = new DataTable();\n        customerTable.Load(reader);\n\n        // Move to the next result set\n        reader.NextResult();\n\n        // Read the second result set\n        DataTable inventoryTable = new DataTable();\n        inventoryTable.Load(reader);\n\n        // Process the data tables as needed\n        // For example, you can merge them or process them separately\n    }\n}