public class CustomerMap : ClassMap<Customer>\n   {\n       public CustomerMap()\n       {\n           Id(x => x.Id);\n           Map(x => x.Name);\n           HasMany(x => x.Orders)\n               .Cascade.All()\n               .Inverse();\n       }\n   }\n\n   public class OrderMap : ClassMap<Order>\n   {\n       public OrderMap()\n       {\n           Id(x => x.Id);\n           Map(x => x.OrderDate);\n           References(x => x.Customer);\n       }\n   }