using System.Windows;\n\nnamespace SyncTreeViewListBox\n{\n    public partial class MainWindow : Window\n    {\n        public MainWindow()\n        {\n            InitializeComponent();\n            DataContext = new MyViewModel\n            {\n                Items = new ObservableCollection<MyDataClass>\n                {\n                    new MyDataClass { Name = "Item 1" },\n                    new MyDataClass { Name = "Item 2" }\n                }\n            };\n        }\n\n        private void TreeView_SelectedItemChanged(object sender, RoutedPropertyChangedEventArgs<object> e)\n        {\n            if (DataContext is MyViewModel viewModel)\n            {\n                viewModel.SelectedItem = e.NewValue as MyDataClass;\n            }\n        }\n    }\n}