using OpenQA.Selenium;\n   using OpenQA.Selenium.Chrome;\n   using System;\n   using System.Collections.ObjectModel;\n\n   class Program\n   {\n       static void Main(string[] args)\n       {\n           // Initialize the ChromeDriver\n           IWebDriver driver = new ChromeDriver();\n\n           // Navigate to a website\n           driver.Navigate().GoToUrl("https://www.example.com");\n\n           // Perform actions\n           // Back\n           driver.Navigate().Back();\n\n           // Forward\n           driver.Navigate().Forward();\n\n           // Open new tab\n           ((IJavaScriptExecutor)driver).ExecuteScript("window.open();");\n           ReadOnlyCollection<string> tabs = driver.WindowHandles;\n           driver.SwitchTo().Window(tabs[1]); // Switch to new tab\n\n           // Close tab\n           driver.Close();\n           driver.SwitchTo().Window(tabs[0]); // Switch back to original tab\n\n           // Open new window\n           ((IJavaScriptExecutor)driver).ExecuteScript("window.open('https://www.example.com','_blank');");\n           ReadOnlyCollection<string> windows = driver.WindowHandles;\n           driver.SwitchTo().Window(windows[1]); // Switch to new window\n\n           // Close window\n           driver.Close();\n           driver.SwitchTo().Window(windows[0]); // Switch back to original window\n\n           // Clean up\n           driver.Quit();\n       }\n   }