using System;\nusing System.IO;\nusing iText.Kernel.Pdf;\nusing iText.Kernel.Utils;\n\nclass Program\n{\n    static void Main()\n    {\n        string sourcePdfPath = "path/to/your/source.pdf";\n        string outputDirectory = "path/to/output/directory/";\n\n        // Ensure the output directory exists\n        Directory.CreateDirectory(outputDirectory);\n\n        using (PdfDocument sourcePdf = new PdfDocument(new PdfReader(sourcePdfPath)))\n        {\n            PdfSplitter splitter = new PdfSplitter(sourcePdf)\n            {\n                SplitPageEvent = (pdfDocument, pageNumber) =>\n                {\n                    string fileName = Path.Combine(outputDirectory, $"split_{pageNumber}.pdf");\n                    return new PdfDocument(new PdfWriter(fileName));\n                }\n            };\n\n            splitter.SplitByPageCount(1);\n        }\n\n        Console.WriteLine("PDF split completed.");\n    }\n}