// During login or session start\n   if (Session["Username"] == null && Request.Cookies["UserInfo"] != null)\n   {\n       Session["Username"] = Request.Cookies["UserInfo"]["Username"];\n       Session["UserID"] = Request.Cookies["UserInfo"]["UserID"];\n       Session["Email"] = Request.Cookies["UserInfo"]["Email"];\n   }\n   else if (Session["Username"] == null)\n   {\n       // Fetch user info from database if cookies are also expired\n       string userID = User.Identity.Name;\n       var userInfo = GetUserInfoFromDatabase(userID);\n       Session["Username"] = userInfo.Username;\n       Session["UserID"] = userInfo.UserID;\n       Session["Email"] = userInfo.Email;\n   }