using System;\nusing System.Globalization;\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Example dropdown list selected time\n        string selectedTime = "8:00AM"; // Replace this with ddlStartTime.SelectedItem.Text\n\n        // Get today's date\n        DateTime today = DateTime.Today;\n\n        // Combine today's date with the selected time\n        DateTime dateTime;\n        if (DateTime.TryParseExact(selectedTime, "h:mmtt", CultureInfo.InvariantCulture, DateTimeStyles.None, out dateTime))\n        {\n            dateTime = today.Add(dateTime.TimeOfDay);\n            Console.WriteLine(dateTime.ToString("MM/dd/yyyy hh:mm:ss.fff"));\n        }\n        else\n        {\n            Console.WriteLine("String was not recognized as a valid DateTime.");\n        }\n    }\n}