using System.IO;\nusing System.Web.UI;\nusing System.Web.UI.WebControls;\n\n// Assuming you already have an instance of DataGrid called dataGrid\npublic string GetDataGridHtml(DataGrid dataGrid)\n{\n    if (dataGrid == null) throw new ArgumentNullException(nameof(dataGrid));\n    \n    // Ensure the DataGrid is bound to data\n    dataGrid.DataBind();\n    \n    using (StringWriter stringWriter = new StringWriter())\n    {\n        using (HtmlTextWriter htmlWriter = new HtmlTextWriter(stringWriter))\n        {\n            // Render the DataGrid control into the HtmlTextWriter\n            dataGrid.RenderControl(htmlWriter);\n        }\n        \n        // Return the rendered HTML\n        return stringWriter.ToString();\n    }\n}