using System;\nusing System.Drawing;\n\nclass Program\n{\n    static void Main()\n    {\n        string imagePath = "path_to_your_image.jpg";\n\n        using (Image image = Image.FromFile(imagePath))\n        {\n            // Get the horizontal resolution (DPI)\n            float horizontalResolution = image.HorizontalResolution;\n            // Get the vertical resolution (DPI)\n            float verticalResolution = image.VerticalResolution;\n\n            // Get the dimensions (width and height in pixels)\n            int width = image.Width;\n            int height = image.Height;\n\n            Console.WriteLine($"Horizontal Resolution: {horizontalResolution} DPI");\n            Console.WriteLine($"Vertical Resolution: {verticalResolution} DPI");\n            Console.WriteLine($"Width: {width} pixels");\n            Console.WriteLine($"Height: {height} pixels");\n\n            // Check if the resolution is too low (example threshold: 300 DPI)\n            if (horizontalResolution < 300 || verticalResolution < 300)\n            {\n                Console.WriteLine("Warning: The image resolution is too low and may appear pixelated in the end-product.");\n            }\n            else\n            {\n                Console.WriteLine("The image resolution is sufficient for printing.");\n            }\n        }\n    }\n}