@using (Ajax.BeginForm("AddRecord", "MyController", new AjaxOptions { UpdateTargetId = "grid", InsertionMode = InsertionMode.InsertAfter, OnSuccess = "onSuccess" }))\n{\n    <input type="text" name="Property1" />\n    <input type="text" name="Property2" />\n    <!-- Add more input fields as needed -->\n    <input type="submit" value="Add Record" />\n}\n\n<table id="grid">\n    @foreach (var item in Model)\n    {\n        Html.RenderPartial("_GridRow", item);\n    }\n</table>