Start: <asp:TextBox ID="txtStart" runat="server" /> (hh:mm)\n<br />\nFinish: <asp:TextBox ID="txtFinish" runat="server" /> (hh:mm)\n<br />\n<asp:CustomValidator\n    ID="cvTime"\n    ControlToValidate="txtFinish"\n    ErrorMessage="Finish time must be later than the start time."\n    OnServerValidate="ValidateTimes"\n    Display="Static"\n    runat="server" />\n\n<script runat="server">\n    protected void ValidateTimes(object source, ServerValidateEventArgs args)\n    {\n        DateTime startTime, finishTime;\n\n        // Try to parse the start and finish times\n        bool isStartValid = DateTime.TryParseExact(txtStart.Text, "HH:mm", null, System.Globalization.DateTimeStyles.None, out startTime);\n        bool isFinishValid = DateTime.TryParseExact(txtFinish.Text, "HH:mm", null, System.Globalization.DateTimeStyles.None, out finishTime);\n\n        if (isStartValid && isFinishValid)\n        {\n            // Validate that the finish time is greater than or equal to the start time\n            args.IsValid = finishTime >= startTime;\n        }\n        else\n        {\n            // If either time is invalid, set validation to false\n            args.IsValid = false;\n        }\n    }\n</script>