using (SqlCommand cmd = new SqlCommand("YourStoredProcedure", connection))\n{\n    cmd.CommandType = CommandType.StoredProcedure;\n\n    // Execute the command and get the reader\n    using (SqlDataReader reader = cmd.ExecuteReader())\n    {\n        // Check if there are any rows\n        if (reader.HasRows)\n        {\n            // Move to the next result to get the XML\n            reader.NextResult();\n            \n            // Execute the ExecuteXmlReader method\n            using (XmlReader xmlReader = cmd.ExecuteXmlReader())\n            {\n                // Process the XML data\n                // Your XML processing code here\n            }\n        }\n        else\n        {\n            // Handle the case where there are no rows\n            Console.WriteLine("No rows returned.");\n        }\n    }\n}