private void SaveButton_Click(object sender, EventArgs e)\n{\n    // Assuming your DataGridView is bound to a BindingSource\n    BindingSource bindingSource = (BindingSource)dataGridView1.DataSource;\n    \n    // End the edit operation on the DataGridView\n    bindingSource.EndEdit();\n    \n    // Get the DataTable from the BindingSource\n    DataTable dataTable = (DataTable)bindingSource.DataSource;\n    \n    // Accept changes on the DataTable\n    dataTable.AcceptChanges();\n    \n    // Optionally, you can update the DataSet if needed\n    // dataset.Tables["YourTableName"].Merge(dataTable, true, MissingSchemaAction.Add);\n\n    // Display a message to confirm the save operation\n    MessageBox.Show("Changes saved successfully!", "Save", MessageBoxButtons.OK, MessageBoxIcon.Information);\n}