protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        BindEmptyGrid();\n    }\n}\n\nprivate void BindEmptyGrid()\n{\n    // Create a DataTable with a single dummy row\n    DataTable dt = new DataTable();\n    dt.Columns.Add("Column1");\n    dt.Columns.Add("Column2");\n    DataRow dr = dt.NewRow();\n    dt.Rows.Add(dr);\n\n    // Bind the DataTable to the GridView\n    GridView1.DataSource = dt;\n    GridView1.DataBind();\n\n    // Clear the dummy row to show only the footer row\n    if (GridView1.Rows.Count == 1)\n    {\n        GridView1.Rows[0].Visible = false;\n    }\n\n    // Set the footer row visibility to true\n    GridView1.ShowFooter = true;\n}\n\nprotected void GridView1_RowCommand(object sender, GridViewCommandEventArgs e)\n{\n    if (e.CommandName == "Add")\n    {\n        // Code to add data from the footer row to the database\n        // Extract data from footer row and insert it into the database\n        \n        TextBox txtColumn1 = (TextBox)GridView1.FooterRow.FindControl("txtColumn1");\n        TextBox txtColumn2 = (TextBox)GridView1.FooterRow.FindControl("txtColumn2");\n        \n        string column1Value = txtColumn1.Text;\n        string column2Value = txtColumn2.Text;\n        \n        // Insert these values into the database\n        // InsertDataIntoDatabase(column1Value, column2Value);\n\n        // Rebind the GridView to show updated data\n        BindEmptyGrid();\n    }\n}