using System.Linq;\n\npublic class MyRepository\n{\n    private readonly MyDataContext _context;\n\n    public MyRepository(MyDataContext context)\n    {\n        _context = context;\n    }\n\n    public IQueryable<MyEntity> GetEntities()\n    {\n        string cacheKey = "MyEntities";\n        return CacheHelper.GetOrAdd(cacheKey, () => _context.MyEntities.ToList(), 60).AsQueryable();\n    }\n\n    public void AddEntity(MyEntity entity)\n    {\n        _context.MyEntities.InsertOnSubmit(entity);\n        _context.SubmitChanges();\n        \n        // Invalidate cache\n        CacheHelper.Remove("MyEntities");\n    }\n    \n    // Other methods...\n}