// Shared Models\npublic class SharedEntity\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n}\n\n// Data Context 1\npublic class Context1 : DataContext\n{\n    public Table<SharedEntity> SharedEntities;\n    public Table<SpecificEntity1> SpecificEntities1;\n}\n\n// Data Context 2\npublic class Context2 : DataContext\n{\n    public Table<SharedEntity> SharedEntities;\n    public Table<SpecificEntity2> SpecificEntities2;\n}\n\n// Service Layer\npublic class DataService\n{\n    private readonly Context1 _context1;\n    private readonly Context2 _context2;\n\n    public DataService(Context1 context1, Context2 context2)\n    {\n        _context1 = context1;\n        _context2 = context2;\n    }\n\n    public SharedEntity GetSharedEntity(int id)\n    {\n        return _context1.SharedEntities.SingleOrDefault(e => e.Id == id) ?? \n               _context2.SharedEntities.SingleOrDefault(e => e.Id == id);\n    }\n\n    // Other methods to handle data operations\n}