using Microsoft.SharePoint.Client;\n\npublic void EnableContentTypeManagement(string siteUrl, string listTitle)\n{\n    ClientContext context = new ClientContext(siteUrl);\n    Web web = context.Web;\n    List list = web.Lists.GetByTitle(listTitle);\n\n    list.ContentTypesEnabled = true;\n    list.Update();\n\n    context.ExecuteQuery();\n}\n\n// Usage example:\nstring siteUrl = "https://yoursharepointsite.com/sites/yoursite";\nstring listTitle = "YourDocumentLibrary";\n\nEnableContentTypeManagement(siteUrl, listTitle);