using System;\nusing Microsoft.Office.Interop.Word;\n\nclass Program\n{\n    static void Main()\n    {\n        Application wordApp = new Application();\n        wordApp.Visible = false; // Make sure the Word application is not visible\n\n        try\n        {\n            Document doc = wordApp.Documents.Open(@"C:\path\to\your\document.docx");\n            doc.PrintOut();\n            doc.Close(false); // Close the document without saving changes\n        }\n        finally\n        {\n            wordApp.Quit(false); // Quit the Word application without saving changes\n        }\n    }\n}