using System;\nusing System.IO;\nusing System.Reflection;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        if (args.Length == 0)\n        {\n            Console.WriteLine("Usage: DocumentationGenerator <assembly-path>");\n            return;\n        }\n\n        string assemblyPath = args[0];\n        Assembly assembly = Assembly.LoadFrom(assemblyPath);\n\n        using (StreamWriter writer = new StreamWriter("Documentation.txt"))\n        {\n            foreach (Type type in assembly.GetTypes())\n            {\n                writer.WriteLine($"Type: {type.FullName}");\n                writer.WriteLine(new string('-', 50));\n\n                foreach (MemberInfo member in type.GetMembers())\n                {\n                    writer.WriteLine($"{member.MemberType}: {member.Name}");\n                }\n\n                writer.WriteLine();\n            }\n        }\n\n        Console.WriteLine("Documentation generated successfully.");\n    }\n}