using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\nusing Microsoft.AspNetCore.Mvc;\n\nnamespace YourNamespace.Controllers\n{\n    [Route("scrape")]\n    [ApiController]\n    public class ScrapeController : ControllerBase\n    {\n        [HttpPost]\n        public async Task<IActionResult> Scrape([FromForm] string url)\n        {\n            if (string.IsNullOrEmpty(url))\n                return BadRequest("URL is required");\n\n            using (HttpClient client = new HttpClient())\n            {\n                try\n                {\n                    string content = await client.GetStringAsync(url);\n                    // Here you would parse and extract the needed data from 'content'\n                    // For simplicity, we're just returning the entire content\n                    return Content(content, "text/html");\n                }\n                catch (Exception ex)\n                {\n                    return StatusCode(500, "Error occurred while scraping: " + ex.Message);\n                }\n            }\n        }\n    }\n}