// Server-side method to register the updated dropdown list values\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            // Initial population of dropdown list\n            PopulateDropdownList();\n        }\n\n        // Register the dropdown list values for event validation\n        foreach (ListItem item in DropDownList1.Items)\n        {\n            Page.ClientScript.RegisterForEventValidation(DropDownList1.UniqueID, item.Value);\n        }\n    }